configuration.jsScript:
if(metadata.hasOwnProperty("ss_alarm_level")){
    if(metadata.ss_alarm_level>1){
        return false
    }
}

if(!metadata.hasOwnProperty("ss_alarm_level_1_cfg"))
    return false;
var rule = JSON.parse(metadata.ss_alarm_level_1_cfg.replace(/\\/g,""));
//[{\"Key\":\"加速度\",\"IndeterminateRules\":{\"min\":\"-0.5\",\"max\":\"0.5\"}}]
/* alarm rule tables */

for (var i = 0; i < rule.length; i++) {
    if (msg.hasOwnProperty(rule[i].Key)) {
        var val_max = msg[rule[i].Key+"_max"];
        var val_min = msg[rule[i].Key+"_min"];
        if(rule[i].hasOwnProperty("IndeterminateRules")){
            var min = parseFloat(rule[i].IndeterminateRules.min);
            var max = parseFloat(rule[i].IndeterminateRules.max);
            if (!isNaN(min)){
                if(val_min < min){
                    return true;
                }
            }
            if(!isNaN(max)){
                if(val_max > max){
                    return true;
                }
            }
        }
        
    }
}
return false;

