var newMsg = {};
var newMsgType = 'POST_ATTRIBUTES_REQUEST';
if (metadata.hasOwnProperty("ss_alarm_device_id")) {
    var ids = JSON.parse(metadata["ss_alarm_device_id"]);

    var isExist = false;
    for (var key in ids) {
        if (ids.hasOwnProperty(key)  && ids[key].hasOwnProperty('dev_id')) {
            if(ids[key].dev_id === metadata.dev_id){
                if(ids[key].hasOwnProperty('alarm_level')){
                    ids[key].alarm_level = metadata.alarm_level
                }
                isExist = true;
                break;
            }
        }
    }
    if (!isExist) {
        ids.push({"dev_id":metadata.dev_id, "alarm_level":metadata.alarm_level});
        newMsg["alarm_device_id"] = JSON.stringify(ids);
    } else {
        newMsg["alarm_device_id"] = JSON.stringify(ids);
    }

} else {
    newMsg["alarm_device_id"] = JSON.stringify([{"dev_id":metadata.dev_id, "alarm_level":metadata.alarm_level}]);
}
return {
    msg: newMsg,
    metadata: metadata,
    msgType: newMsgType
};
