function findId(id,alarmDeviceIds){
    for (var key in alarmDeviceIds) {
        if (alarmDeviceIds.hasOwnProperty(key)) {
            if(!alarmDeviceIds[key].hasOwnProperty('dev_id')){
                continue;
            }
            var element = alarmDeviceIds[key];
            if(element.dev_id === id){
                return true;
            }
        }
    }
    return false;
}
function hasHighLevelId(alarmDeviceIds){
    for (var key in alarmDeviceIds) {
        if (alarmDeviceIds.hasOwnProperty(key)) {
            if(!alarmDeviceIds[key].hasOwnProperty('dev_id')){
                continue;
            }
            if(!alarmDeviceIds[key].hasOwnProperty('alarm_level')){
                continue;
            }
            var element = alarmDeviceIds[key];
            if(element.alarm_level === 2){
                return true;
            }
        }
    }
    return false;
}


function tryRules(rule,alarmDeviceIds) {
        //check orangeRules first, then blueRules
    blueRules = [];
    orangeRules = [];
    if(typeof rule === undefined)
         return  '';
    if(rule.hasOwnProperty("blueRules")){
        blueRules = rule.blueRules;
    }
    if(rule.hasOwnProperty("orangeRules")){
        orangeRules = rule.orangeRules;
    }
    //橙色预警规则
    //msg中包含资产中报警的设备（keys:true）
    //input: msg.devA,msg.devB
    //rule: [{andRule:[devA,devB]},{andRule:[devC]}]
    //
    var orange = false;
    if(orangeRules !== null && orangeRules.hasOwnProperty('length')){
        for (var idx = 0; idx < orangeRules.length; idx++) {
            if(typeof orangeRules[idx] === undefined)
                continue;
            if(orangeRules[idx].hasOwnProperty('andRule')){
                andRules = orangeRules[idx].andRule;
                for (var jdx = 0; jdx < andRules.length; jdx++) {
                    var alarmDev = andRules[jdx];
                    if(!findId(alarmDev,alarmDeviceIds)){
                        break;
                    }
                    if (jdx === andRules.length - 1)
                        orange = true;
                }
                if (!orange) {
                    continue;
                } else{
                    break;
                }
            }
        }
    }
    if (orange)
        return 'orange';

    var blue = false;
    if(blueRules !== null && blueRules.hasOwnProperty('length')){
        for (idx = 0; idx < blueRules.length; idx++) {
            if(typeof blueRules[idx] === undefined)
                continue;
            if(blueRules[idx].hasOwnProperty('andRule')){
                andRules = blueRules[idx].andRule;
                for ( jdx = 0; jdx < andRules.length; jdx++) {
                    alarmDev = andRules[jdx];
                    if(!findId(alarmDev,alarmDeviceIds)){
                        break;
                    }
                    if (jdx === andRules.length - 1)
                        blue = true;
                }
                if (!blue) { // test next rule (or)
                    continue;
                } else{
                    break;
                }
            }
        }
    }
    if (blue)
        return 'blue';
}


function nextRelation(rule,alarmDeviceIds) {
    var condition = 'default';
    switch (tryRules(rule,alarmDeviceIds)) {
        case 'blue':
            // code
            condition = "BLUE";
            break;
        case 'orange':
            condition = "YELLOW";
            break;
        default:
            condition = "GREEN";
            break;
    }
    return condition;
}

var msgtmp = {}
msgType = 'POST_ATTRIBUTES_REQUEST'
if(!metadata.hasOwnProperty("ss_asset_warning_level")
||!metadata.hasOwnProperty("ss_warning_rule_cfg")
||!metadata.hasOwnProperty("ss_alarm_device_id"))
{
    msgtmp["asset_warning_level"] = "GREEN"
}
if(metadata.hasOwnProperty("ss_asset_warning_level")&&metadata.hasOwnProperty("ss_warning_rule_cfg")
&&metadata.hasOwnProperty("ss_alarm_device_id")){
    var rule = JSON.parse(metadata.ss_warning_rule_cfg);
    var alarmDeviceIds = JSON.parse(metadata.ss_alarm_device_id);
    var warningLevel = metadata.ss_asset_warning_level;
    if(warningLevel == "RED")
    {
        msgtmp["asset_warning_level"] = warningLevel;
    }
    if(hasHighLevelId(alarmDeviceIds)){
        msgtmp["asset_warning_level"] = "YELLOW"
    }else{
        metadata.ss_asset_warning_level = nextRelation(rule,alarmDeviceIds)
        msgtmp["asset_warning_level"] = metadata.ss_asset_warning_level;
    }
}



return {msg: msgtmp, metadata: metadata, msgType: msgType};

